// Import.
const md5 = require('md5');

// Constants.
const DEFAULT_DATA = '';
const DEFAULT_SALT = '';

/**
 * Hash data using salt.
 * @param {string} data Data to hash.
 * @param {string} [salt] Salt.
 * @returns {string} Salted MD5 hash.
 */
function hash(data = DEFAULT_DATA, salt = DEFAULT_SALT) {
  // Return hash.
  return md5(data + salt);
}

// Export.
module.exports = hash;
