// Import.
const assert = require('assert');
const saltedMd5 = require('./index');

// Prepare data and salt.
const data = 'Some data.';
const salt = 'SUPER-S@LT!';
const correctResult = '1b25469a2e4b157f02dd1fea3b5f1e9a';

// Get salted MD5.
const saltedHash = saltedMd5(data, salt);

// Test.
assert.equal(saltedHash, correctResult, 'Wrong hash.');

// Inform test passed.
console.log('Test passed!');
